import Context from './MyContext';
import React, { Component } from 'react';
import MyContext from './MyContext';

class Provider extends Component {
  state = {
    currentInput: 0,
    prevInput: null,
    method: null,
    pop: 9,
  };

  handleClick = (symbol) => {
    console.log('poo');
    // this.setState({ pop: symbol });
  };

  render() {
    return (
      <MyContext.Provider
        value={{
          state: this.state,
          handleClick: (sym) => console.log(sym),
        }}
      >
        {this.props.children}
      </MyContext.Provider>
    );
  }
}
export default Provider;
