import React, { Component } from 'react';
import styled from 'styled-components';

// Styling each button
const BtnStyled = styled.div`
  background-color: rgb(50, 50, 50);
  color: white;
  cursor: pointer;
  &:nth-child(17) {
    grid-column: 1 / span 2;
  }
  &:nth-child(-n + 3) {
    color: black;
    background-color: rgb(162, 162, 162);
  }
  &:nth-child(4n) {
    background-color: rgb(240, 147, 41);
    font-size: 3rem;
  }

  &:last-child {
    background-color: rgb(240, 147, 41);
  }
  &:nth-child(8) span {
    font-size: 2.1rem;
    font-weight: bolder;
    transform: translate(-0.8rem, 1.8rem);
  }
  &:nth-child(17) span {
    transform: translate(-4.5rem, 1.7rem);
  }
  &:nth-child(12) span {
    transform: translate(-50%, 40%);
  }
  &:nth-child(4) span {
    font-size: 3.5rem;
    font-weight: normal;
    transform: translate(-45%, 30%);
  }
  border-radius: 70px;
  text-align: center;
  position: relative;
`;

const StyledSpan = styled.span`
  position: absolute;
  transform: translate(-51%, 48%);
  font-size: 3rem;
`;

//Button
class Button extends Component {
  state = {
    value: 0,
  };
  render() {
    return (
      <BtnStyled>
        <StyledSpan onClick={this.props.handleClick}>
          {this.props.sign}
        </StyledSpan>
      </BtnStyled>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    handleClick: () => dispatch({ type: 'CLICKED', payload: this.props.sign }),
  };
};

export default connect(null, mapDispatchToProps)(Button);
