import React, { Component } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';

// Styled Component holds everything but time above the number buttons
const DisplayStyled = styled.div`
  height: 16.2rem;
  background-color: rgba(0, 0, 0, 0.98);
  position: relative;
`;

//Styled Component that holds input numbers and output
const NumbersPanel = styled.div`
  padding: 0 20px 20px 20px;
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  position: absolute;
  width: 100%;
  height: 12.9rem;
  background-color: rgba(0, 0, 0, 0.98);
  bottom: 0;
  color: white;
  font-size: 7.78rem;
  float: right;
  text-align: right;
`;
class Display extends Component {
  render() {
    const inputLength = this.props.input.length;

    console.log(inputLength);
    return (
      <DisplayStyled>
        <NumbersPanel>
          <div style={{ paddingBottom: '0rem' }}>{this.props.input}</div>
        </NumbersPanel>
      </DisplayStyled>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    input: state.input,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(Display);
