import Context from './MyContext';
import React, { Component } from 'react';
import MyContext from './MyContext';

class Provider extends Component {
  state = {
    currentInput: 0,
    prevInput: null,
    method: null,
  };

  handleClick = (symbol) => {
    console.log(symbol);
    if (this.state.currentInput === 0) this.setState({ pop: symbol });
  };

  render() {
    return (
      <MyContext.Provider
        value={{
          state: this.state,
          handleClick: (sym) => this.handleClick(sym),
        }}
      >
        {this.props.children}
      </MyContext.Provider>
    );
  }
}
export default Provider;
