import Context from './Context';
import React, { Component } from 'react';

class Provider extends Component {
  state = {
    currentInput: 0,
    prevInput: null,
    method: null,
    pop: 9,
  };

  render() {
    return (
      <Context.Provider pop={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}
export default Provider;
