import Context from './MyContext';
import React, { Component } from 'react';
import MyContext from './MyContext';

class Provider extends Component {
  state = {
    currentInput: 0,
    prevInput: null,
    method: null,
  };

  handleClick = (symbol) => {
    const { currentInput } = this.state;
    const intSymbol = Number(symbol);
    console.log(intSymbol);
    console.log(currentInput.length);

    if (!isNaN(intSymbol) || (currentInput === 0 && currentInput.length <= 8)) {
      if (currentInput === 0) {
        console.log('w');
        this.setState({ currentInput: symbol });
      } else if (currentInput !== 0) {
        this.setState({ currentInput: currentInput + symbol });
      }
    } else if (symbol === '.') {
      if (currentInput === 0 || currentInput.indexOf('.') === -1) {
        this.setState({ currentInput: currentInput + symbol });
      }
    }
  };

  render() {
    return (
      <MyContext.Provider
        value={{
          state: this.state,
          handleClick: (sym) => this.handleClick(sym),
        }}
      >
        {this.props.children}
      </MyContext.Provider>
    );
  }
}
export default Provider;
