import React, { Component } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';

// Styled Component holds everything but time above the number buttons
const DisplayStyled = styled.div`
  background-color: rgba(0, 0, 0, 0.98);
  height: 16.9rem;
  position: relative;
`;

//Styled Component that holds input numbers and output
const NumbersPanelStyled = styled.div`
  background-color: rgba(0, 0, 0, 0.98);
  height: 10.9rem;
  text-align: right;
`;
const StyledDiv = styled.div`
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  color: white;
  font-size: 10.9rem;
  line-height: 10.9rem;
  width: 100%;

  /* padding: 0 20px 20px 20px; */
  padding: 0 1.9rem 1.9rem 1.9rem;

  position: absolute;
  bottom: 0;
  float: right;
  text-align: right;
`;
class Display extends Component {
  render() {
    const inputLength = this.props.input.length;

    console.log(inputLength);
    return (
      <DisplayStyled>
        <NumbersPanelStyled>
          <StyledDiv>{this.props.input}</StyledDiv>
        </NumbersPanelStyled>
      </DisplayStyled>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    input: state.input,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(Display);
