import React, { Component } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';

// Styled Component holds everything but time above the number buttons
const DisplayStyled = styled.div`
  height: 16.2rem;
  background-color: rgba(0, 0, 0, 0.98);
  /* position: relative; */
`;

//Styled Component that holds input numbers and output
const NumbersPanelStyled = styled.div`
  padding: 0 20px 20px 20px;
  /* font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif; */
  bottom: 0;
  /* position: absolute; */
  width: 100%;
  height: 10.9rem;
  background-color: rgba(0, 0, 0, 0.98);
  display: flex;
  flex-direction: column;
  /* color: white;
  font-size: 7.78rem;
  float: right;
  text-align: right; */
`;
const StyledDiv = styled.div`
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  color: white;
  font-size: 7.78rem;
  float: right;
  text-align: right;
  align-items: flex-end;
  justify-content: flex-end;
`;
class Display extends Component {
  render() {
    const inputLength = this.props.input.length;

    console.log(inputLength);
    return (
      <DisplayStyled>
        <NumbersPanelStyled>
          <StyledDiv>{this.props.input}</StyledDiv>
        </NumbersPanelStyled>
      </DisplayStyled>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    input: state.input,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(Display);
